mapper.select:
select gid,gname,state,price,option1,option2,delivery,dcost,dtime,count,detail,sid,gdate,source1,fname1 
from (select rownum as rnum,g.gid gid,gname,state,price,option1,option2,delivery,dcost,dtime,count,detail,sid,gdate,source1,fname1 from goods g,image i  where g.gid=i.gid and g.gid like #{brend}||'%') 
where rnum>=#{first} and rnum<=#{end}
